import React from "react";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import { useForm } from "react-hook-form";
import FormControl from "@material-ui/core/FormControl";
import Alert from "@material-ui/lab/Alert";

import NotExistProvider from "./NotExistProviders";
import NotExistMark from "./NotExisteMark";

import {
  TextFieldController,
  NumberFieldController,
  SelectFieldController,
} from "../../../common/inputs";

import unit from '../../../../const/unit'

const UNITS = unit

const useStyles = makeStyles((theme) => ({
  paper: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "#ffff",
    padding: 15,
    borderRadius: 10,
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
    borderRadius: 10,
    backgroundColor: "#0a043c",
  },
  logo: {
    width: 250,
  },
}));

export default function CreateProduct({ onSubmit, mark, provider }) {
  const classes = useStyles();

  const {
    handleSubmit,
    control,
    errors,
    reset,
    formState: { isDirty, isSubmitting },
  } = useForm();
  const defaultValues = {
    provider: "",
    marca: "",
    unit: "",
  };
  function resetForm() {
    reset(defaultValues);
  }
  return (
    <Container component="main" maxWidth="lg" style={{ position: "relative" }}>
      <CssBaseline />
      <div className={classes.paper}>
        <Alert style={{ position: "absolute", left: "10px" }} severity="info">
          Crea tus productos desde este lugar
        </Alert>
        <img
          alt=""
          src={
            "data:image/png;base64,"
          }
          className={classes.logo}
        />
        <form
          className={classes.form}
          onSubmit={handleSubmit((data) => {
            onSubmit(data, resetForm);
          })}
        >
          <Grid container spacing={3}>
            <Grid item xs={12} sm={6}>
              <TextFieldController
                control={control}
                name="name"
                label="Nombre del producto"
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextFieldController
                control={control}
                name="barcode"
                label="Codigo"
                required
              />
            </Grid>
            <Grid item xs={12} sm={5}>
              <NumberFieldController
                control={control}
                name="price"
                label="Precio de producto"
                required
              />
            </Grid>
            <Grid item xs={12} sm={5}>
              <NumberFieldController
                control={control}
                name="cost"
                label="Costo de producto"
                required
              />
            </Grid>
            <Grid item xs={12} sm={2}>
              <TextFieldController
                control={control}
                name="count"
                label="Cantidad"
                type="number"
                required
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>
            <Grid item xs={12} sm={4}>
              <NotExistProvider />
              <SelectFieldController
                control={control}
                helperText="Por favor selecciona un proveedor"
                name="provider"
                label="Proveedor"
                options={provider}
              />
            </Grid>
            <Grid item xs={12} sm={4}>
              <NotExistMark />
              <SelectFieldController
                control={control}
                helperText="Por favor selecciona una marca"
                name="marca"
                label="Marca"
                options={mark}
              />
            </Grid>
            <Grid item xs={12} sm={4} style={{ marginTop: "30px" }}>
              <SelectFieldController
                control={control}
                helperText="Por favor selecciona una unidad"
                name="unit"
                label="Unidad"
                options={UNITS}
              />
            </Grid>
          </Grid>
          <FormControl fullWidth style={{ marginTop: "30px" }}>
            <TextFieldController
              control={control}
              name="description"
              label="DescripciÃ³n"
              multiline
              rows={2}
              variant="outlined"
            />
          </FormControl>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            disabled={!isDirty || isSubmitting}
          >
            Crear Producto
          </Button>
        </form>
      </div>
    </Container>
  );
}
